#include <iostream>
int main() { int n; std::cout << "Enter the number of elements: "; std::cin >> n; int* arr = new int[n]; std::cout << "Enter " << n << " elements:\n"; for (int i = 0; i < n; i++) { std::cin >> arr[i]; } for (int i = 0; i < n - 1; i++) { for (int j = 0; j < n - i - 1; j++) { if (arr[j] > arr[j + 1]) { int temp = arr[j]; arr[j] = arr[j + 1]; arr[j + 1] = temp; } } } std::cout << "Sorted array:\n"; for (int i = 0; i < n; i++) { std::cout << arr[i] << " "; } std::cout << std::endl; delete[] arr; return 0; }